"use strict";

function Cube() {
  var me = this;

  this.cube_map = {};
  this.set_cube_map = function(map) {
    this.cube_map = map;
  }
  this.card_tmpl_fn = undefined;

  this.Init = function() {
    me.card_tmpl_fn = doT.template($('#card_tmpl').get(0).text);

    for (var group in me.cube_map) {
      var cards = me.cube_map[group];
      cards.sort(function(a, b) {
          var cmp = a['act_type'].localeCompare(b['act_type']);
          if (cmp != 0) return cmp;
          else if (a['conv_cost'] !== b['conv_cost'])
            return a['conv_cost'] - b['conv_cost'];
          else if (a['act_type'] !== 'Land') {
            cmp = a['mana_cost'].localeCompare(b['mana_cost']);
            if (cmp != 0) return cmp;
          }

          return a['name'].localeCompare(b['name']);
      });
      _Render(group, cards, true);
    }

    // let's bind the sort functions
    $('#cube_content .tab-pane').each(function(idx) {
        var $tab = $(this);
        var group = $tab.attr('id');
        // upper case first letter
        group = group.slice(0, 1).toUpperCase() + group.slice(1);
        var cards = me.cube_map[group];

        $tab.find('.mana').on('click', function(e) {
            cards.sort(_ManaCostCmp);
            _Render(group, cards);
        });
        $tab.find('.type').on('click', function(e) {
            cards.sort(_TypeCmp);
            _Render(group, cards);
        });
        $tab.find('.name').on('click', function(e) {
            cards.sort(_NameCmp);
            _Render(group, cards);
        });
    });
  }

  // on initial render, change the pipes to newlines in text
  function _Render(group, cards, initial) {
    if (typeof initial === 'undefined') inital = false;

    var html = '';
    for (var idx in cards) {
      var card = cards[idx];
      if (initial) card['text'] = card['text'].replace(/\|/g, '<br>');
      html += me.card_tmpl_fn(card);
    }
    $('#' + group.toLowerCase() + ' tbody').html(html);
  }

  function _ManaCostCmp(a, b) {
    if (a['conv_cost'] !== b['conv_cost']) {
      return a['conv_cost'] - b['conv_cost'];
    }

    // return 0 if both lands (we are never comparing lands and others)
    if (a['act_type'] === 'Land') return 0;
    else return a['mana_cost'].localeCompare(b['mana_cost']);
  }

  function _TypeCmp(a, b) {
    return a['act_type'].localeCompare(b['act_type']);
  }

  function _NameCmp(a, b) {
    return a['name'].localeCompare(b['name']);
  }
}

